INSERT INTO `plugin` VALUES ('plugin-7b21a4f0-7cb1-4283-9d8c-155fb4888075', 'api.factba.se', 'fiscalnote', 'https://api.factba.se/openapi.json', 'fiscalnote', 'FiscalNote', 'Use the Biden remarks API to answer questions about statements (spoken or written) made by President Biden. Use the calendar API to answer questions about the White House official calendar. Use the Roll Call API to search for news articles related to Congressional people and proceedings.', 'FiscalNote enables access to select market-leading, real-time data sets for legal, political, and regulatory information', 'https://api.factba.se/static/fn-logo.png', 'collin.stedman@fiscalnote.com', '', '使用拜登言论API回答关于拜登总统所做出的声明（口头或书面）的问题。使用日历API回答关于白宫官方日历的问题。使用Roll Call API搜索与国会人员和程序相关的新闻文章。', 'FiscalNote提供访问市场领先的、实时的、可选择的法律、政治和监管信息数据集。');
